export interface CameraPosition {
  /**
   * 3D world position where the camera is focused on.
   *
   * Fallback is [0,0,0]
   */
  target: [number, number, number];
  /**
   * The camera pitch angle in degree.
   *
   * The value defines the rotation around the local x-axis of the camera.
   *
   * Example:
   * -90° = top view |
   * 0° = frontal view |
   * 90° = bottom view
   */
  pitch: number;
  /**
   * The camera yaw angle in degree.
   *
   * The value defines the rotation around the world y-axis at the target position.
   *
   * Example:
   * 0° = front view |
   * 90° = right view |
   * 180° = back view |
   * 270° = left view
   *
   */
  yaw: number;
  /**
   * Defines the distance of the camera to the target position in meters
   */
  distance: number;
}
