/**
 * State of a single skeleton
 */
export interface SkeletonState {
  /**
   * The states of all bones inside the skeleton
   * Missing bone states or empty value will result in default (rest-pose) values
   */
  boneStates?: BoneState[];
}

export interface BoneState {
  /**
   * The index of the bone inside the skeleton definition
   */
  boneIndex: number;
  /**
   * The local position of the bone. If empty the default (rest-pose) values is taken
   */
  localPosition?: [number, number, number];
  /**
   * The local rotation of the bone in euler angles or as an angle axis representation
   */
  localRotation?: EulerAngles | AngleAxisRotation;
}

export interface EulerAngles {
  x: number;
  y: number;
  z: number;
}

export interface AngleAxisRotation {
  /** Normalized rotation axis */
  axis: [number, number, number];
  /** Angle in degree */
  angle: number;
}
