import { FromEngineMessage } from "../post-messages";

/**
 * Sent as a response for every message to the engine,
 */
export interface AckMessage extends FromEngineMessage<"ack"> {
  payload: {
    /**
     * The nonce that was sent with the message to the engine.
     *
     * Used to identify to what message the ack belongs.
     */
    nonce: string;
    /**
     * An error if the message to the engine could not be processed by the engine.
     */
    error?: {
      message?: string;
    };
  };
}
