import { FromEngineMessage, ToEngineMessage } from "../post-messages";

/**
 * A screenshot of the canvas that is sent to the embedder when the heatmap
 * collection is stopped.
 *
 * The screenshot is used by the embedder to draw the heatmap over it.
 */
// TODO: This should have another name to make it clear it's related to the
//       heat map collection.
export interface CanvasImageUrlMessage
  extends FromEngineMessage<"canvas-image-url"> {
  payload: {
    /**
     * The height of the image.
     */
    height: number;
    /**
     * The width of the image.
     */
    width: number;
    /**
     * The base64 encoded image in the PNG format.
     */
    data: string;
  };
}
