import { ToEngineMessage } from "../post-messages";

// Mouse events from engine to clients
export interface PointerEvent {
  x: number;
  y: number;
  drag: boolean;
  type: string;
  buttons: number;
  pick?: {
    x: number;
    y: number;
  };
  shiftKey: boolean;
}

/**
 * Sent when an edge gets deselected.
 */
export interface PointerEventMessage extends ToEngineMessage<"pointer-event"> {
  /**
   * The index of the edge.
   */
  payload: PointerEvent;
}
