/**
 * A message sent from the engine to the embedder.
 */
export interface FromEngineMessage<Channel> {
  channel: Channel;
  payload: unknown;
}

/**
 * A message sent from the embedder to the engine.
 */
export interface ToEngineMessage<Channel> {
  channel: Channel;
  /**
   * The nonce is a randomly generated string that is re-send by the engine in
   * the ack message.
   */
  nonce: string;
  payload?: unknown;
}

export type FromEnginePostMessage =
  | AckMessage
  | CanvasImageUrlMessage
  | DisplayLoadingUiMessage
  | EngineReadyMessage
  | HideGuiMessage
  | HideLoadingUiMessage
  | KeyDownMessage
  | KeyDownEventMessage
  | KeyUpEventMessage
  | PointerEventMessage
  | SetLoadingProgressMessage
  | CameraPositionChangeMessage
  | EnvironmentRotationChangedMessage
  | GeometryTransformChangeMessage
  | AnnotationTagPositionChangedMessage;

export type ToEnginePostMessage =
  | DownloadScreenshotMessage
  | HighlightLayerMessage
  | MouseEventMessage
  | ResetCameraPositionMessage
  | RotateToVectorMessage
  | UpdateCameraPositionMessage
  | SelectedEdgesMessage
  | SetClearColorMessage
  | SetBackgroundImageMessage
  | SetConnectionInfosMessage
  | SetCutoutTextureMessage
  | SetEdgeSelectorEnabledMessage
  | SetEffectsSettingsMessage
  | SetEmbossingHeightTextureMessage
  | SetEnvironmentMessage
  | SetEnvironmentVisibleMessage
  | SetFullscreenMessage
  | SetGeometryMessage
  | SetGridVisibilityMessage
  | SetHardwareScalingLevelMessage
  | SetLayersMessage
  | SetPpiMessage
  | SetStageMappingsMessage
  | SetSurfaceMaterialMessage
  | SetUseFramingAnimation
  | ToggleFullscreenMessage
  | ToggleInspectorMessage
  | ToggleVrMessage
  | UnhighlightLayerMessage
  | RecenterSkeletonObjectsMessage
  | SetSkeletonStatesMessage
  | SetShadowPlaneVisibilityMessage
  | UpdateEnvironmentRotationMessage
  | UpdateGeometryTransformMessage
  | SetInitialGeometryTransformMessage
  | ResetGeometryTransformMessage
  | PlaceGeometryOnGroundMessage
  | SetAnnotationTagsMessage
  | SetCustomRequestHeaders;

export type PostMessage = FromEnginePostMessage | ToEnginePostMessage;
