import { ToEngineMessage } from "../post-messages";

export interface ScreenshotPayload {
  fileName: string;
  overlayPNG?: ImageBitmap;
  width?: number;
  height?: number;
}

/**
 * Downloads a high-resolution screenshot of the current scene.
 *
 * The download starts automatically once the screenshot has been rendered.
 */
export interface DownloadScreenshotMessage
  extends ToEngineMessage<"download-screenshot"> {
  /**
   * An empty object.
   */
  payload: ScreenshotPayload;
}
