import { GeometryTransform } from "../GeometryTransform";
import { ToEngineMessage } from "../post-messages";

/**
 * Sets the geometry rotation and position to the initial transform values.
 *
 * If no initial transform was defined `{position: [0,0,0], rotation: [0,0,0]}`
 * is used
 */
export interface ResetGeometryTransformMessage
  extends ToEngineMessage<"reset-geometry-transform"> {
  payload: { transitionDuration?: number };
}
