import { ToEngineMessage } from "../post-messages";

/**
 * Rotates the camera to a given vector.
 */
export interface RotateToVectorMessage
  extends ToEngineMessage<"rotate-to-vector"> {
  /**
   * The vector to rotate to.
   *
   * Example: `[0, 0, 1]`
   */
  payload: [number, number, number];
}
