import { AnnotationTag } from "../AnnotationTag";
import { ToEngineMessage } from "../post-messages";

/**
 * Sets the annotation tags that should be displayed in the scene.
 *
 * The engine only renders the tags connector lines and sends the tags screen positions to the
 * embedder via `annotation-tag-position-changed` message. The embedder is responsible for rendering the
 * tags.
 */
export interface SetAnnotationTagsMessage
  extends ToEngineMessage<"set-annotation-tags"> {
  payload: AnnotationTag[];
}
