import { TextureBlob, TextureUrl } from "../Layer";
import { ToEngineMessage } from "../post-messages";

/**
 * A message to set the scene's background image.
 */
export interface SetBackgroundImageMessage
  extends ToEngineMessage<"set-background-image"> {
  payload: {
    texture: string | TextureUrl | Blob | TextureBlob | null;
    options?: {
      scalingMode?: "stretch" | "fill" | "fit";
    };
  };
}
