import { ToEngineMessage } from "../post-messages";

/**
 * Set the scene's clear color (i.e., the background color).
 */
export interface SetClearColorMessage
  extends ToEngineMessage<"set-clear-color"> {
  /**
   * The clear color in HEX format with alpha (e.g., #AD86A3FF).
   */
  payload: string;
}
