import { ToEngineMessage } from "../post-messages";

/**
 * Set the area connection infos for the folding edges.
 */
export interface SetConnectionInfosMessage
  extends ToEngineMessage<"set-connection-infos"> {
  /**
   * An array with
   *
   * 1. The id of the source area.
   * 2. The id of the target area.
   * 3. The center of the folding edge as an array in the form [x, y].
   * 4. The normal vector of the folding edge as an array in the form [x, y].
   * 5. The length of the folding edge.
   */
  payload: ConnectionInfos;
}
export interface ConnectionInfo {
  faceIndex1: number;
  faceIndex2: number;
  line: { p1: { u: number; v: number }; p2: { u: number; v: number } };
}
export type ConnectionInfos = Array<ConnectionInfo>;
