import { ToEngineMessage } from "../post-messages";

/**
 * Sets the cutout mask that is used to render holes the surface texture.
 *
 * The cutout mask is a mask in the PNG format with white and transparent
 * colors.
 */
export interface SetCutoutTextureMessage
  extends ToEngineMessage<"set-cutout-texture"> {
  /**
   * The cutout mask in the PNG format. Can either be a blob or a URL to the
   * cutout texture. An empty string is used to reset it (i.e., setting a
   * texture with no cutouts).
   */
  payload: Blob | string | "";
}
