import { ToEngineMessage } from "../post-messages";

/**
 * Sets whether or not the edge selector should be enabled.
 *
 * When enabled, the edges are visible and when clicked on, a SelectEdgeMessage
 * is received.
 */
export interface SetEdgeSelectorEnabledMessage
  extends ToEngineMessage<"set-edge-selector-enabled"> {
  /**
   * Whether or not the edge selector should be enabled.
   */
  payload: boolean;
}
