import { ToEngineMessage } from "../post-messages";

/**
 * Set the effect settings.
 */
export interface SetEffectsSettingsMessage
  extends ToEngineMessage<"set-effects-settings"> {
  /**
   * The settings.
   *
   * When a field is not send, it is replaced with the default value for that
   * field, overwriting potential previous changes.
   */
  payload: Partial<EffectsSettings>;
}

export interface EffectsSettings {
  /**
   * The field of view in radians.
   *
   * Defaults to 0.75 (approx. 43 degrees).
   */
  fieldOfView: number;
  /**
   * Whether or not the bloom effect should be enabled.
   *
   * Defaults to `true`.
   */
  bloomEnabled: boolean;
  /**
   * The bloom threshold. A number between `0` and `1`.
   *
   * Defaults to `0.9`.
   */
  bloomThreshold: number;
  /**
   * The bloom weight. A number between `0` and `1`.
   *
   * Defaults to `0.05`.
   */
  bloomWeight: number;
  /**
   * Whether or not chromatic aberration should be enabled.
   *
   * Defaults to `false`.
   */
  chromaticAberrationEnabled: boolean;
  /**
   * The amount of chromatic aberration. Can be any positive number but values
   * between `0` and `100` yield the best results.
   *
   * Defaults to `0`.
   */
  chromaticAberrationAmount: number;
  /**
   * Whether or not the grain effect should be enabled.
   *
   * Defaults to `false`.
   */
  grainEnabled: boolean;
  /**
   * The grain intensity. Can be any positive number but values between `0`
   * and `50` yield the best results.
   *
   * Defaults to `30`.
   */
  grainIntensity: number;
  /**
   * The image processing brightness in percent.
   *
   * Defaults to `1`.
   */
  imageProcessingBrightness: number;
  /**
   * The image processing contrast.
   *
   * Defaults to `1`.
   */
  imageProcessingContrast: number;
  /**
   * Whether or not tone mapping should be enabled.
   *
   * Defaults to `false`.
   */
  toneMappingEnabled: boolean;
  /**
   * Whether or not the vignette effect should be enabled.
   *
   * Defaults to `true`.
   */
  vignetteEnabled: boolean;
  /**
   * The weight of the vignette.
   *
   * Defaults to `1.5`.
   */
  vignetteWeight: number;
  /**
   * Whether or not the image should be sharpened.
   *
   * Defaults to `false`.
   */
  sharpenEnabled: boolean;
  /**
   * The sharpen amount.
   *
   * Defaults to `0.3`.
   */
  sharpenAmount: number;
  /**
   * Whether or not dithering should be enabled. With dithering enabled, the
   * effect of color banding is reduced (can often often be seen on a single
   * color background with vignette enabled).
   *
   * Defaults to `true`.
   */
  ditheringEnabled: boolean;
  /**
   * The intensity of the dithering.
   *
   * Defaults to `1 / 255`.
   */
  ditheringIntensity: number;
}
