import { ToEngineMessage } from "../post-messages";

/**
 * Sets the height texture for the embossing.
 *
 * The height texture is a grayscale PNG image that is used to calculate the
 * height of the embossing.
 */
export interface SetEmbossingHeightTextureMessage
  extends ToEngineMessage<"set-embossing-height-texture"> {
  /**
   * The height texture as a blob in the PNG format.
   *
   * If set to `undefined`, the height texture is removed.
   */
  payload: undefined | Blob;
}
