import { ToEngineMessage } from "../post-messages";

/**
 * Set the environment.
 *
 * TODO: Why is this one called "set-environment" and not "set-environment-url"?
 */
export interface SetEnvironmentMessage
  extends ToEngineMessage<"set-environment"> {
  /**
   * Supports all supported formats of BabylonJS. It is recommended to use
   * BabylonJS' own format (either DDS or ENV) for faster loading.
   */
  payload: Environment;
}

export interface Environment {
  /**
   * An url to the environment file.
   */
  filenameHDR?: string;
  /**
   * The data of the HDR. If set, the data is not loaded from the URL provided
   * with the `filenameHDR` field.
   */
  blobHDR?: Blob;
  /**
   * The exposure of the HDR.
   *
   * Defaults to `1`.
   */
  exposureHDR?: number;
  /**
   * The default rotation of the environment in radians.
   *
   * Defaults to `0`.
   */
  defaultRotation?: number;
}
