import { ToEngineMessage } from "../post-messages";

/**
 * Sets the hardware scaling level.
 */
export interface SetHardwareScalingLevelMessage
  extends ToEngineMessage<"set-hardware-scaling-level"> {
  /**
   * The hardware scaling level.
   *
   * A smaller value means a higher quality, a larger value means a lower
   * quality.
   *
   * The default value is `1` which means no scaling. If the value is for
   * example `0.5`, the image will be rendered with twice the screen resolution,
   * resulting in a higher quality image.
   */
  payload: number;
}
