import { GeometryTransform } from "../GeometryTransform";
import { ToEngineMessage } from "../post-messages";

/**
 * Defines the initial geometry rotation and position.
 *
 * The initial transform is used if the transform is reseted.
 */
export interface SetInitialGeometryTransformMessage
  extends ToEngineMessage<"set-initial-geometry-transform"> {
  payload: { transform: GeometryTransform };
}
