import { Layer } from "../Layer";
import { ToEngineMessage } from "../post-messages";

/**
 * A message to set the scene's layers.
 */
export interface SetLayersMessage extends ToEngineMessage<"set-layers"> {
  payload: {
    /**
     * The layers. The key is the id of the layer.
     */
    [layerName: string]: Layer;
  };
}
