import { ToEngineMessage } from "../post-messages";

/**
 * Set the ppi.
 */
export interface SetPpiMessage extends ToEngineMessage<"set-ppi"> {
  /**
   * The PPI. The PPI value is required to properly render textures that have a
   * fixed size (e.g., a micro-embossing with a 5x5mm pattern).
   */
  payload: number;
}
