import { SkeletonState } from "../SkeletonState";
import { ToEngineMessage } from "../post-messages";

/**
 * A message to set the mesh skeleton states
 */
export interface SetSkeletonStatesMessage
  extends ToEngineMessage<"set-skeleton-states"> {
  payload: {
    /**
     * The skeleton states. The key is the name of the skeleton from the mesh.
     */
    [skeletonName: string]: SkeletonState;
  };
}
