import { CameraPosition } from "../Camera";
import { ToEngineMessage } from "../post-messages";

/**
 * Rotates the camera to a given position.
 *
 * The position can be partial information. In that case the other position values are kept as is.
 */
export interface UpdateCameraPositionMessage
  extends ToEngineMessage<"update-camera-position"> {
  payload: { changes: Partial<CameraPosition>; transitionDuration?: number };
}
