import { GeometryTransform } from "../GeometryTransform";
import { ToEngineMessage } from "../post-messages";

/**
 * Translates and rotates the camera to a given values.
 *
 * The transform data can be partial information. In that case the other transform values are kept as is.
 */
export interface UpdateGeometryTransformMessage
  extends ToEngineMessage<"update-geometry-transform"> {
  payload: { changes: Partial<GeometryTransform>; transitionDuration?: number };
}
