let _accessToken = undefined;
/**
 * Sets the default access token used by the library.
 *
 * @param accessToken The default access token to set.
 */
export function setAccessToken(accessToken) {
    _accessToken = accessToken;
}
/**
 * Gets the default access token.
 *
 * @returns The default access token.
 */
export function getAccessToken() {
    return _accessToken;
}
