/**
 * More fine grained control over the base URL, allowing to set the PCM and
 * embed separately.
 */
export interface ExtendedBaseUrl {
    /**
     * The base URL (origin) for the PCM.
     */
    pcm: string;
    /**
     * The base URL (origin) for the embed.
     */
    embed: string;
}
/**
 * Sets the default base url used by the library.
 *
 * The default base url is `https://dreamcomposer.de`.
 *
 * @param baseUrl The default base url to set.
 */
export declare function setBaseUrl(baseUrl: string | ExtendedBaseUrl): void;
/**
 * Gets the default base url.
 *
 * @returns The default base url.
 */
export declare function getBaseUrl(): string | ExtendedBaseUrl;
/**
 * Gets the PCM base url.
 *
 * @returns The default PCM base url.
 */
export declare function getPcmBaseUrl(): string;
/**
 * Gets the embed base url.
 *
 * @returns The default embed base url.
 */
export declare function getEmbedBaseUrl(): string;
