let _baseUrl = "https://dreamcomposer.de";
/**
 * Sets the default base url used by the library.
 *
 * The default base url is `https://dreamcomposer.de`.
 *
 * @param baseUrl The default base url to set.
 */
export function setBaseUrl(baseUrl) {
    _baseUrl = baseUrl;
    if (typeof _baseUrl === "string" && _baseUrl.endsWith("/")) {
        _baseUrl = _baseUrl.substring(0, _baseUrl.length - 1);
    }
    else if (typeof _baseUrl === "object") {
        if (_baseUrl.pcm.endsWith("/")) {
            _baseUrl.pcm = _baseUrl.pcm.substring(0, _baseUrl.pcm.length - 1);
        }
        if (_baseUrl.embed.endsWith("/")) {
            _baseUrl.embed = _baseUrl.embed.substring(0, _baseUrl.embed.length - 1);
        }
    }
}
/**
 * Gets the default base url.
 *
 * @returns The default base url.
 */
export function getBaseUrl() {
    return _baseUrl;
}
/**
 * Gets the PCM base url.
 *
 * @returns The default PCM base url.
 */
export function getPcmBaseUrl() {
    return typeof _baseUrl === "string" ? _baseUrl : _baseUrl.pcm;
}
/**
 * Gets the embed base url.
 *
 * @returns The default embed base url.
 */
export function getEmbedBaseUrl() {
    return typeof _baseUrl === "string" ? _baseUrl : _baseUrl.embed;
}
