import { DcController } from "../DcController.js";
/**
 * Sets the cutout texture.
 *
 * The cutout texture is a PNG image that is used remove cutouts from the
 * geometry (i.e., creating "holes"). Pixels must either be fully transparent or
 * fully opaque. Transparent pixels are the ones that are cut out (i.e.,
 * "removed"). The color of the pixels is irrelevant.
 *
 * @param dcController The DcController object obtained from the init function.
 * @param blobOrUrl A blob or URL to the cutout texture in the PNG format. Can
 *                  be undefined to reset the texture.
 * @returns A promise that resolves when the cutout texture has been set.
 */
export declare function setCutoutTexture(dcController: DcController, blobOrUrl?: string | Blob): Promise<void>;
