/**
 * Resets the camera position to its initial position.
 *
 * This only resets the zoom and shift of the camera, not the rotation.
 *
 * @param dcController The DcController object obtained from the init function.
 * @returns A promise that resolves when the camera position has been reset.
 */
export function resetCameraPosition(dcController) {
    return dcController.postMessage({
        channel: "reset-camera-position",
        payload: {},
    }, {
        memorize: false,
    });
}
