/**
 * Rotates the camera position to the given vector.
 *
 * @param dcController The DcController object obtained from the init function.
 * @returns A promise that resolves when the camera position has been updated.
 */
export function rotateToVector(dcController, vector) {
    return dcController.postMessage({
        channel: "rotate-to-vector",
        payload: vector,
    }, {
        memorize: false,
    });
}
