/**
 * Enables or disables framing animations
 *
 * @param dcController The DcController object obtained from the init function.
 * @param enabled A boolean value to designate desired state
 * @returns A promise that resolves when the setting has been applied
 */
export function setUseFramingAnimation(dcController, enabled) {
    return dcController.postMessage({
        channel: "set-use-framing-animation",
        payload: enabled,
    });
}
