/**
 * Toggles whether or not the viewer is shown in fullscreen mode.
 *
 * Also works for the preview.
 *
 * @param dcController The DcController object obtained from the init function.
 * @returns A promise that resolves when fullscreen has been toggled.
 */
export function toggleFullscreen(dcController) {
    if (document.fullscreenElement) {
        document.exitFullscreen();
    }
    else {
        dcController.container.requestFullscreen();
    }
    return Promise.resolve();
}
