/**
 * Toggles whether or not the BabylonJS inspector is shown.
 *
 * @param dcController The DcController object obtained from the init function.
 * @returns A promise that resolves when inspector has been toggled.
 */
export function toggleInspector(dcController) {
    if (!dcController.isLoaded()) {
        throw new Error("Inspector cannot be toggled when viewer is not loaded.");
    }
    return dcController.postMessage({
        channel: "toggle-inspector",
        payload: {},
    }, {
        memorize: false,
    });
}
