/**
 * Rotates the scene environment to the desired value.
 *
 * @param dcController The DcController object obtained from the init function.
 * @param rotation The target rotation in degree
 * @param transitionDuration Animation duration in seconds. Default is `0`
 * @returns A promise that resolves when the environment rotation has been updated.
 */
export function updateEnvironmentRotation(dcController, rotation, transitionDuration) {
    return dcController.postMessage({
        channel: "update-environment-rotation",
        payload: { rotation, transitionDuration },
    }, {
        memorize: false,
    });
}
