/**
 * Checks if the given hex color is valid and if it is, converts it to a full
 * ARGB color in uppercase.
 *
 * @param color The color to validate.
 * @returns `false` if the color is invalid, otherwise the color in ARGB format.
 */
export function parseColorHex(color) {
    const regex = /^#[0-9a-fA-F]{8}$|^#[0-9a-fA-F]{6}$|^#[0-9a-fA-F]{4}$|^#[0-9a-fA-F]{3}$/gm;
    if (!regex.test(color)) {
        return false;
    }
    if (color.length === 4 || color.length === 5) {
        color = color
            .split("")
            .map((c) => c + c)
            .join("")
            .slice(1); // Fix double # at the beginning
    }
    if (color.length === 7) {
        color = color + "FF";
    }
    return color.toUpperCase();
}
